/**	definition of the User Manager Utilities module, which provides static utility functions	*/

var um_utils = function () {}

um_utils.ignore_favicon = function (request, response) {
	if (request.url === '/favicon.ico') {
		response.writeHead(200, {'Content-Type': 'image/x-icon'} );
		response.end();
		return true;
	}
	else
		return false;
}

/**	read in file with Yelp categories	*/
um_utils.get_categories = function  () {
	//var fs = require('fs');
	return JSON.parse(require('fs').readFileSync('categories.json', 'utf8'));
}

// add class to NodeJS's available modules
module.exports = um_utils;